package myproject;

public class samplevariable_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//addition of two numbers 
     int number1=20;
     int number2=30;
     int result = number1 + number2;
     System.out.println(" sum of two numbers is : " + result);
     
     //substraction of two numbers 
     int result_2 = number1 - number2;
     System.out.println(" sum of two numbers is : " +result_2);
     
     //multiplication of two numbers 
     int result_3 = number1 * number2;
     System.out.println(" sum of two numbers is : " +result_3);
     
     // division of two numbers 
     int result_4 = number1 / number2;
     System.out.println(" sum of two numbers is : " +result_4);
     
	}

}
